mapper.select:
select *
from userinfo
where user_Id = #{userId}
limit 1;

select id_card
from userinfo
where id_card = #{idcard}
limit 1;

mapper.update:
update userinfo
set real_name= #{real_name},id_card= #{id_card},student_id= #{student_id}
where user_id= #{id}

update account
set password= #{password}
where user_id= #{id}

update account
set email= #{email}
where user_id= #{id}

update userinfo
set user_name= #{user_name},signature=#{signature}
where user_id= #{id}

update userinfo
set head_portrait= #{head_portrait}
where user_id= #{id}

update account
set status= 3
where user_id= #{id}

update account
set power= #{power}
where user_id= #{id}
